import React from "react";
import "./style.css";
import logo from "./images/logo.png"; // Add your logo image here
import arrowImage from "./images/arrow-1.png"; // Make sure the path is correct

import image9 from "./images/image-9.png";
import image16 from "./images/image-16.png";
import image4 from "./images/image-4.png";
import image19 from "./images/image-19.png";
import image from "./images/image.png";
import image10 from "./images/image-10.png";
import image12 from "./images/image-12.png";
import image7 from "./images/image-7.png";
import image13 from "./images/image-13.png";

const currencyData = [
  { label: "USD - KES", icon: image9, buying: "129.30", selling: "132.68" },
  { label: "EUR - KES", icon: image16, buying: "139.30", selling: "142.68" },
  { label: "USD - UGX", icon: image4, buying: "3490.30", selling: "3520.68" },
  { label: "USD - TZS", icon: image19, buying: "2310.30", selling: "2340.68" },
  { label: "USD - NGN", icon: image, buying: "460.30", selling: "470.68" },
  { label: "USD - JPY", icon: image10, buying: "110.30", selling: "112.68" },
  { label: "GBP - KES", icon: image12, buying: "179.30", selling: "182.68" },
  { label: "USD - CNY", icon: image7, buying: "6.30", selling: "6.68" },
  { label: "CNY - KES", icon: image13, buying: "19.30", selling: "20.68" },
];

const FigmaDesign = () => {
  const formatDate = (date) => {
    const options = { weekday: 'long', year: 'numeric', month: 'long', day: 'numeric' };
    return date.toLocaleDateString('en-US', options);
  };

  const currentDate = formatDate(new Date());

  return (
    <div className="container">
      <img src={logo} alt="Logo" className="logo" />
      <div className="regulation-text">
        Licensed & Regulated by the Central Bank of Kenya & the Bank of Uganda
      </div>
      <div className="exchange-rate-container">
        <div className="exchange-rate-title">Exchange Rate</div>
        <div className="exchange-rate-date">{currentDate}</div>
      </div>
      <div className="header">
        <span className="header-text">BUYING</span>
        <span className="header-text">SELLING</span>
      </div>
      <div className="currency-list">
        {currencyData.map((currency, index) => (
          <div key={index} className="currency-row">
            <div className="currency-label">
              <img
                className="currency-icon"
                src={currency.icon}
                alt={`${currency.label} icon`}
              />
              <span>{currency.label}</span>
            </div>
            <img src={arrowImage} alt="Arrow" className="arrow-icon" />
            <div className="currency-action">{currency.buying}</div>
            <div className="currency-action">{currency.selling}</div>
          </div>
        ))}
      </div>
    </div>
  );
};

function App() {
  return <FigmaDesign />;
}

export default App;
