import React from "react";
import "./style.css"; // Make sure the CSS file is correctly imported
import image9 from "./images/image-9.png";
import image16 from "./images/image-16.png";
import image4 from "./images/image-4.png";
import image19 from "./images/image-19.png";
import image from "./images/image.png";
import image10 from "./images/image-10.png";
import image12 from "./images/image-12.png";
import image7 from "./images/image-7.png";
import image13 from "./images/image-13.png";
import image18 from "./images/image-18.png";

const currencyData = [
  { label: "USD - KES", icon: image9 },
  { label: "EUR - KES", icon: image16 },
  { label: "USD - UGX", icon: image4 },
  { label: "USD - TZS", icon: image19 },
  { label: "USD - NGN", icon: image },
  { label: "USD - JPY", icon: image10 },
  { label: "GBP - KES", icon: image12 },
  { label: "USD - CNY", icon: image7 },
  { label: "CNY - KES", icon: image13 },
];

export const FigmaDesign = () => {
  return (
    <div className="container">
      <div className="header">
        <span className="header-text">BUYING</span>
        <span className="header-text">SELLING</span>
      </div>
      <div className="currency-list">
        {currencyData.map((currency, index) => (
          <div key={index} className="currency-row">
            <div className="currency-label">
              <img
                className="currency-icon"
                src={currency.icon}
                alt={`${currency.label} icon`}
              />
              <span>{currency.label}</span>
            </div>
            <div className="currency-action"></div>
            <div className="currency-action"></div>
          </div>
        ))}
      </div>
    </div>
  );
};

function App() {
  return <FigmaDesign />;
}

export default App;
