import React, { useState, useEffect } from "react";
import "./style.css";

import logo from "./images/logo.png"; // Add your logo image here
import image9 from "./images/image-9.png";
import image16 from "./images/image-16.png";
import image4 from "./images/image-4.png";
import image19 from "./images/image-19.png";
import image from "./images/image.png";
import image10 from "./images/image-10.png";
import image12 from "./images/image-12.png";
import image7 from "./images/image-7.png";
import image13 from "./images/image-13.png";
import arrow from "./images/arrow-1.png"; // Add your arrow image here

const FigmaDesign = () => {
  // Declare individual variables for each currency pair
  let usdKesBuy = "";
  let usdKesSell = "";
  let eurKesBuy = "";
  let eurKesSell = "";
  let usdUgxBuy = "";
  let usdUgxSell = "";
  let usdTzsBuy = "";
  let usdTzsSell = "";
  let usdNgnBuy = "";
  let usdNgnSell = "";
  let usdJpyBuy = "";
  let usdJpySell = "";
  let gbpKesBuy = "";
  let gbpKesSell = "";
  let usdCnyBuy = "";
  let usdCnySell = "";
  let cnyKesBuy = "";
  let cnyKesSell = "";

  // Set up state for the currency data
  const [currencyData, setCurrencyData] = useState([
    { label: "USD-KES", icon: image9, buying: usdKesBuy, selling: usdKesSell },
    { label: "EUR-KES", icon: image16, buying: eurKesBuy, selling: eurKesSell },
    { label: "USD-UGX", icon: image4, buying: usdUgxBuy, selling: usdUgxSell },
    { label: "USD-TZS", icon: image19, buying: usdTzsBuy, selling: usdTzsSell },
    { label: "USD-NGN", icon: image, buying: usdNgnBuy, selling: usdNgnSell },
    { label: "USD-JPY", icon: image10, buying: usdJpyBuy, selling: usdJpySell },
    { label: "GBP-KES", icon: image12, buying: gbpKesBuy, selling: gbpKesSell },
    { label: "USD-CNY", icon: image7, buying: usdCnyBuy, selling: usdCnySell },
    { label: "CNY-KES", icon: image13, buying: cnyKesBuy, selling: cnyKesSell },
  ]);

  useEffect(() => {
    // Fetch the data from the backend
    fetch('http://localhost:3001/')
      .then(response => response.json())
      .then(data => {
        updateRates(data);
      })
      .catch(error => console.error('Error fetching rates:', error));
  }, []);

  const updateRates = (data) => {
    // Iterate over the data and update each currency pair variable
    data.forEach(currency => {
      if (currency["USD-KES"]) {
        usdKesBuy = currency["USD-KES"]?.[0] || ""; // Buy value
        usdKesSell = currency["USD-KES"]?.[1] || ""; // Sell value
      }
      if (currency["EUR-KES"]) {
        eurKesBuy = currency["EUR-KES"]?.[0] || "";
        eurKesSell = currency["EUR-KES"]?.[1] || "";
      }
      if (currency["USD-UGX"]) {
        usdUgxBuy = currency["USD-UGX"]?.[0] || "";
        usdUgxSell = currency["USD-UGX"]?.[1] || "";
      }
      if (currency["USD-TZS"]) {
        usdTzsBuy = currency["USD-TZS"]?.[0] || "";
        usdTzsSell = currency["USD-TZS"]?.[1] || "";
      }
      if (currency["USD-NGN"]) {
        usdNgnBuy = currency["USD-NGN"]?.[0] || "";
        usdNgnSell = currency["USD-NGN"]?.[1] || "";
      }
      if (currency["USD-JPY"]) {
        usdJpyBuy = currency["USD-JPY"]?.[0] || "";
        usdJpySell = currency["USD-JPY"]?.[1] || "";
      }
      if (currency["GBP-KES"]) {
        gbpKesBuy = currency["GBP-KES"]?.[0] || "";
        gbpKesSell = currency["GBP-KES"]?.[1] || "";
      }
      if (currency["USD-CNY"]) {
        usdCnyBuy = currency["USD-CNY"]?.[0] || "";
        usdCnySell = currency["USD-CNY"]?.[1] || "";
      }
      if (currency["CNY-KES"]) {
        cnyKesBuy = currency["CNY-KES"]?.[0] || "";
        cnyKesSell = currency["CNY-KES"]?.[1] || "";
      }
    });

    // Update state for the currency data to render it dynamically
    const updatedCurrencyData = [
      { label: "USD-KES", icon: image9, buying: usdKesBuy, selling: usdKesSell },
      { label: "EUR-KES", icon: image16, buying: eurKesBuy, selling: eurKesSell },
      { label: "USD-UGX", icon: image4, buying: usdUgxBuy, selling: usdUgxSell },
      { label: "USD-TZS", icon: image19, buying: usdTzsBuy, selling: usdTzsSell },
      { label: "USD-NGN", icon: image, buying: usdNgnBuy, selling: usdNgnSell },
      { label: "USD-JPY", icon: image10, buying: usdJpyBuy, selling: usdJpySell },
      { label: "GBP-KES", icon: image12, buying: gbpKesBuy, selling: gbpKesSell },
      { label: "USD-CNY", icon: image7, buying: usdCnyBuy, selling: usdCnySell },
      { label: "CNY-KES", icon: image13, buying: cnyKesBuy, selling: cnyKesSell },
    ];

    setCurrencyData(updatedCurrencyData);
  };

  const formatDate = (date) => {
    const day = date.getDate();
    const suffix = day % 10 === 1 && day !== 11 ? 'ST' : day % 10 === 2 && day !== 12 ? 'ND' : day % 10 === 3 && day !== 13 ? 'RD' : 'TH';
    const options = { weekday: 'long', day: 'numeric', month: 'long' };
    const formattedDate = date.toLocaleDateString('en-US', options).toUpperCase();
    return formattedDate.replace(day, `${day}${suffix}`);
  };

  const currentDate = formatDate(new Date());

  return (
    <div className="container">
      <img src={logo} alt="Logo" className="logo" />
      <div className="regulation-text">
        Licensed & Regulated by the Central Bank of Kenya & the Bank of Uganda
      </div>
      <div className="exchange-rate-container">
        <div className="exchange-rate-title asset-regular">EXCHANGE RATE</div>
        <div className="exchange-rate-date">{currentDate}</div>
      </div>
      <div className="header">
        <div className="header-left">
          <span className="header-text">BUYING</span>
        </div>
        <div className="header-right">
          <span className="header-text">SELLING</span>
        </div>
      </div>
      <div className="currency-list">
        {currencyData.map((currency, index) => (
          <div key={index} className="currency-row">
            <div className="currency-icon-container">
              <img
                className="currency-icon"
                src={currency.icon}
                alt={`${currency.label} icon`}
              />
            </div>
            <div className="currency-label-container">
              <span>{currency.label}</span>
            </div>
            <div className="currency-action-container">
              <img src={arrow} alt="Arrow" className="arrow-icon" />
            </div>
            <div className="currency-action">{currency.buying}</div>
            <div className="currency-action">{currency.selling}</div>
          </div>
        ))}
      </div>
    </div>
  );
};

function App() {
  return <FigmaDesign />;
}

export default App;
