import React, { useState, useEffect } from "react";
import "./style.css";
import logo from "./images/logo.png"; // Add your logo image here
import image9 from "./images/image-9.png";
import image16 from "./images/image-16.png";
import image4 from "./images/image-4.png";
import image19 from "./images/image-19.png";
import image from "./images/image.png";
import image10 from "./images/image-10.png";
import image12 from "./images/image-12.png";
import image7 from "./images/image-7.png";
import image13 from "./images/image-13.png";
import arrow from "./images/arrow-1.png"; // Add your arrow image here

const initialCurrencyData = [
  { label: "USD-KES", icon: image9, buying: " ", selling: " " },
  { label: "EUR-KES", icon: image16, buying: " ", selling: " " },
  { label: "USD-UGX", icon: image4, buying: " ", selling: " " },
  { label: "USD-TZS", icon: image19, buying: " ", selling: " " },
  { label: "USD-NGN", icon: image, buying: " ", selling: " " },
  { label: "USD-JPY", icon: image10, buying: " ", selling: " " },
  { label: "GBP-KES", icon: image12, buying: " ", selling: " " },
  { label: "USD-CNY", icon: image7, buying: " ", selling: " " },
  { label: "CNY-KES", icon: image13, buying: " ", selling: " " },
];

const FigmaDesign = () => {
  const [currencyData, setCurrencyData] = useState(initialCurrencyData);

  useEffect(() => {
    fetch('http://localhost:3001/')
      .then(response => response.json())
      .then(data => {
        console.log('Fetched data:', data); // Log the fetched data for debugging
        updateRates(data);
      })
      .catch(error => console.error('Error fetching rates:', error));
  }, []);

  const updateRates = (data) => {
    const updatedCurrencyData = currencyData.map(currency => {
      // Remove spaces to ensure correct matching with the backend format
      const label = currency.label.replace(/\s/g, '-');
      const matchingRate = data.find(entry => entry.label === label);

      if (matchingRate) {
        return {
          ...currency,
          buying: matchingRate.buying,
          selling: matchingRate.selling,
        };
      }
      return currency;
    });
    setCurrencyData(updatedCurrencyData);
  };

  const formatDate = (date) => {
    const day = date.getDate();
    const suffix =
      day % 10 === 1 && day !== 11
        ? "ST"
        : day % 10 === 2 && day !== 12
        ? "ND"
        : day % 10 === 3 && day !== 13
        ? "RD"
        : "TH";
    const options = { weekday: "long", day: "numeric", month: "long" };
    const formattedDate = date.toLocaleDateString("en-US", options).toUpperCase();
    return formattedDate.replace(day, `${day}${suffix}`);
  };

  const currentDate = formatDate(new Date());

  return (
    <div className="container">
      <img src={logo} alt="Logo" className="logo" />
      <div className="regulation-text">
        Licensed & Regulated by the Central Bank of Kenya & the Bank of Uganda
      </div>
      <div className="exchange-rate-container">
        <div className="exchange-rate-title asset-regular">EXCHANGE RATE</div>
        <div className="exchange-rate-date">{currentDate}</div>
      </div>
      <div className="header">
        <div className="header-left">
          <span className="header-text">BUYING</span>
        </div>
        <div className="header-right">
          <span className="header-text">SELLING</span>
        </div>
      </div>
      <div className="currency-list">
        {currencyData.map((currency, index) => (
          <div key={index} className="currency-row">
            <div className="currency-icon-container">
              <img
                className="currency-icon"
                src={currency.icon}
                alt={`${currency.label} icon`}
              />
            </div>
            <div className="currency-label-container">
              <span>{currency.label}</span>
            </div>
            <div className="currency-action-container">
              <img src={arrow} alt="Arrow" className="arrow-icon" />
            </div>
            <div className="currency-action">{currency.buying}</div>
            <div className="currency-action">{currency.selling}</div>
          </div>
        ))}
      </div>
    </div>
  );
};

function App() {
  return <FigmaDesign />;
}

export default App;
