import React, { useState, useEffect } from "react";
import "./style.css";
import logo from "./images/logo.png"; // Add your logo image here
import image9 from "./images/image-9.png";
import image16 from "./images/image-16.png";
import image4 from "./images/image-4.png";
import image19 from "./images/image-19.png";
import image from "./images/image.png";
import image10 from "./images/image-10.png";
import image12 from "./images/image-12.png";
import image7 from "./images/image-7.png";
import image13 from "./images/image-13.png";
import arrow from "./images/arrow-1.png"; // Add your arrow image here

const FigmaDesign = () => {
  const [currencyData, setCurrencyData] = useState([]);

  useEffect(() => {
    const fetchCurrencyRates = async () => {
      try {
        const response = await fetch("http://localhost:3001/"); // URL of your backend endpoint
        const backendData = await response.json();

        const updatedCurrencyData = currencyData.map(currency => {
          const matchingRate = backendData.find(item => 
            Object.keys(item).some(key => key.includes(currency.label.split(' - ')[0]))); // Match label part

          if (matchingRate) {
            // Extract matching buying and selling values
            const rateKeys = Object.keys(matchingRate);
            const buyingRate = matchingRate[rateKeys[0]]; // First rate value
            const sellingRate = matchingRate[rateKeys[1]]; // Second rate value
            return { ...currency, buying: buyingRate, selling: sellingRate };
          }
          return currency; // Return original if no match found
        });

        setCurrencyData(updatedCurrencyData);
      } catch (error) {
        console.error("Error fetching currency rates:", error);
      }
    };

    fetchCurrencyRates(); // Fetch rates when component mounts
  }, []);

  return (
    <div className="container">
      <img src={logo} alt="Logo" className="logo" />
      <div className="regulation-text">
        Licensed & Regulated by the Central Bank of Kenya & the Bank of Uganda
      </div>
      <div className="exchange-rate-container">
        <div className="exchange-rate-title asset-regular">EXCHANGE RATE</div>
        <div className="exchange-rate-date">{new Date().toLocaleDateString()}</div>
      </div>
      <div className="header">
        <div className="header-left">
          <span className="header-text">BUYING</span>
        </div>
        <div className="header-right">
          <span className="header-text">SELLING</span>
        </div>
      </div>
      <div className="currency-list">
        {currencyData.map((currency, index) => (
          <div key={index} className="currency-row">
            <div className="currency-icon-container">
              <img
                className="currency-icon"
                src={currency.icon}
                alt={`${currency.label} icon`}
              />
            </div>
            <div className="currency-label-container">
              <span>{currency.label}</span>
            </div>
            <div className="currency-action-container">
              <img src={arrow} alt="Arrow" className="arrow-icon" />
            </div>
            {/* Displaying fetched rates */}
            <div className="currency-action">{currency.buying}</div>
            <div className="currency-action">{currency.selling}</div>
          </div>
        ))}
      </div>
    </div>
  );
};

function App() {
  return <FigmaDesign />;
}

export default App;
